-- Create "categories" table
CREATE TABLE "categories" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "name" character varying NOT NULL, PRIMARY KEY ("id"));
-- Create index "categories_name_key" to table: "categories"
CREATE UNIQUE INDEX "categories_name_key" ON "categories" ("name");
-- Create "movies" table
CREATE TABLE "movies" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "title" character varying NOT NULL, "year" bigint NOT NULL, "category_movies" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "movies_categories_movies" FOREIGN KEY ("category_movies") REFERENCES "categories" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create index "movies_title_key" to table: "movies"
CREATE UNIQUE INDEX "movies_title_key" ON "movies" ("title");
-- Create "characters" table
CREATE TABLE "characters" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "name" character varying NOT NULL, "actor" character varying NOT NULL, "movie_characters" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "characters_movies_characters" FOREIGN KEY ("movie_characters") REFERENCES "movies" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create index "character_name_actor" to table: "characters"
CREATE UNIQUE INDEX "character_name_actor" ON "characters" ("name", "actor");
-- Create "languages" table
CREATE TABLE "languages" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "name" character varying NOT NULL, PRIMARY KEY ("id"));
-- Create index "languages_name_key" to table: "languages"
CREATE UNIQUE INDEX "languages_name_key" ON "languages" ("name");
-- Create "movie_quotes" table
CREATE TABLE "movie_quotes" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "quote" character varying NOT NULL, "context" character varying NOT NULL, "character_quotes" bigint NOT NULL, "language_quotes" bigint NULL, "movie_quotes" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "movie_quotes_characters_quotes" FOREIGN KEY ("character_quotes") REFERENCES "characters" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "movie_quotes_languages_quotes" FOREIGN KEY ("language_quotes") REFERENCES "languages" ("id") ON UPDATE NO ACTION ON DELETE SET NULL, CONSTRAINT "movie_quotes_movies_quotes" FOREIGN KEY ("movie_quotes") REFERENCES "movies" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create index "movie_quotes_quote_key" to table: "movie_quotes"
CREATE UNIQUE INDEX "movie_quotes_quote_key" ON "movie_quotes" ("quote");
